CREATE OR REPLACE EDITIONABLE PROCEDURE "%USER_NAME%"."ADD_AUDIT_COLUMNS_AND_TRIGGER" ( 
    p_table_name    IN VARCHAR2, 
    p_user_table    IN VARCHAR2 := 'tbl_user' 
) AS 
BEGIN 
    -- Add Audit Columns 
    EXECUTE IMMEDIATE 'ALTER TABLE ' || p_table_name || ' ADD ( 
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL 
    )'; 

    EXECUTE IMMEDIATE 'ALTER TABLE ' || p_table_name || ' ADD ( 
        created_by NUMBER NOT NULL 
    )'; 

    EXECUTE IMMEDIATE 'ALTER TABLE ' || p_table_name || ' ADD ( 
        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL 
    )'; 

    EXECUTE IMMEDIATE 'ALTER TABLE ' || p_table_name || ' ADD ( 
        updated_by NUMBER NOT NULL 
    )'; 

    -- Add Foreign Key Constraints 
    EXECUTE IMMEDIATE 'ALTER TABLE ' || p_table_name || ' ADD CONSTRAINT ' || 
                       p_table_name || '_created_by_fk FOREIGN KEY (created_by) REFERENCES ' || 
                       p_user_table || ' (id)'; 

    EXECUTE IMMEDIATE 'ALTER TABLE ' || p_table_name || ' ADD CONSTRAINT ' || 
                       p_table_name || '_updated_by_fk FOREIGN KEY (updated_by) REFERENCES ' || 
                       p_user_table || ' (id)'; 

    -- Create Trigger for Automatically Updating `updated_at` and `updated_by` 
    EXECUTE IMMEDIATE ' 
        CREATE OR REPLACE TRIGGER ' || p_table_name || '_audit_trg 
        BEFORE INSERT OR UPDATE ON ' || p_table_name || ' 
        FOR EACH ROW 
        DECLARE 
            v_user_id NUMBER; 
        BEGIN 
            -- Fetch the user ID based on APP_USER 
            SELECT id INTO v_user_id FROM tbl_user WHERE email = v(''APP_USER'') FETCH FIRST ROW ONLY; 

            -- Set created_at and created_by on INSERT 
            IF INSERTING THEN 
                :NEW.created_at := CURRENT_TIMESTAMP; 
                :NEW.created_by := v_user_id; 
            END IF; 

            -- Always set updated_at and updated_by 
            :NEW.updated_at := CURRENT_TIMESTAMP; 
            :NEW.updated_by := v_user_id; 
        END;'; 

END; 
/
