n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "%USER_NAME%"."TBL_USER_AUDIT_TRG" 
        BEFORE INSERT OR UPDATE ON TBL_USER 
        FOR EACH ROW 
        DECLARE 
            v_user_id NUMBER; 
        BEGIN 
            -- Fetch the user ID based on APP_USER 
            SELECT id INTO v_user_id FROM tbl_user WHERE email = v('APP_USER') FETCH FIRST ROW ONLY; 

            -- Set created_at and created_by on INSERT 
            IF INSERTING THEN 
                :NEW.created_at := CURRENT_TIMESTAMP; 
                :NEW.created_by := v_user_id; 
            END IF; 

            -- Always set updated_at and updated_by 
            :NEW.updated_at := CURRENT_TIMESTAMP; 
            :NEW.updated_by := v_user_id; 
        END;
/
